$(document).ready(function(){$(function(){$(".toggle-more").click(function(){var a=$(this).attr("id"),b=a.replace("toggle-","");$("#card-"+b).toggleClass("move-up")})}),$(function(){$(".toggle-less").click(function(){var a=$(this).attr("id"),b=a.replace("toggle-less-","");$("#card-"+b).removeClass("move-up")})}),$(document).scroll(function(){$(".move-up").delay(1e3).removeClass("move-up")})});